import React from "react";
import "./feature-image.css";
import guitarsImage from "../assets/guitars.jpg"; // Import the image


export const FeatureImage = () => {
    const handleScrollToShop = () => {
        const shopTitleElement = document.getElementById("adam-shop");
        if (shopTitleElement) {
          shopTitleElement.scrollIntoView({ behavior: "smooth" });
        }
      };
    return (
        <div className="img-wrapper">
            <a href="#" className="guitars-button" onClick={handleScrollToShop}>
        See Available Guitars
      </a>
            <img className="feature-img" src={guitarsImage} alt="img" />
        </div>
  )}
